const metricConsts = flow.get("metricConsts");
const metric = msg.payload.metric;

const skeleton = {
    "name": {
        "type": "string",
        "validator": (name, obj) => {
            if(name.length < 3 || name.length > 64) {
                throw new Error("Name length must be 3 to 64 characters.")
            }
        }
    },
    "slave_id": {
        "type": "number",
        "validator": (slaveId, obj) => {
            if(slaveId < 1 || slaveId > 255) {
                throw new Error("Slave ID must be in range [1,255]")
            }
        }
    },
    "function_code": {
        "type": "number",
        "validator": (code, obj) => {
            if(code !== 3) {
                throw new Error("Only function code 3 (Read Registers) is implemented")
            }
        }
    },
    "register_start": {
        "type": "number",
        "validator": (reg, obj) => {
            if(reg < 1 || reg > 10000) {
                throw new Error("Register start must be in range [1, 10000].");
            }
        }
    },
    "data_format": {
        "type": "string",
        "validator": (format, obj) => true    // Handled by the <select>
    },
    "byte_order": {
        "type": "string",
        "validator": (order, obj) => {
            const dataSize = metricConsts.dataFormats[obj.data_format];
            const orders = metricConsts.byteOrder[dataSize];
            if(!(orders.includes(order))) {
                throw new Error("Invalid order " + order + ", must be in " + orders);
            }
        }
    },
    "refresh_rate": {
        "type": "number",
        "validator": (rate, obj) => {
            if(rate < 1) {
                throw new Error("Refresh rate must be a positive non-zero number");
            }
        }
    }
}


for (const key in skeleton) {
    if(metric[key] == undefined) {
        throw new Error("Field " + key + " must have a value");
    }

    if(typeof metric[key] !== skeleton[key].type) {
        throw new Error("Type mismatch for field " + key 
            + ". Expected: " + skeleton[key].type + ", found: " 
            + typeof metric[key]);
    }

    skeleton[key].validator(metric[key], metric);
}


return msg;

