const data = msg.payload;
const metric = flow.get("selectedMetric");

const flip = bytes => {
    let copy = [...bytes]
    let i = 1;
    while(i < bytes.length) {
        let temp = copy[i];
        copy[i] = copy[i-1];
        copy[i-1] = temp;
        i += 2;
    }
    return copy;
}

// shouldFlip, bigEndian
const formatInfo = {
    "AB": [false, true],
    "BA": [false, false],
    "ABCD": [false, true],
    "DCBA": [false, false],
    "ABCDEFGH": [false, true],
    "HGFEDCBA": [false, true],
    "BADC": [true, true],
    "CDAB": [true, false],
    "BADCFEHG": [true, true],
    "GHEFCDAB": [true, false]
}[metric.byte_order];

const converter = {
    "int16": v => v.getInt16(0),
    "uint16": v => v.getUint16(0),
    "int32": v => v.getInt32(0),
    "uint32": v => v.getUint32(0),
    "float32": v => v.getFloat32(0),
    "float64": v => v.getFloat64(0),
}[metric.data_format];

const format = bytes => {
    if(formatInfo[0]) {
        bytes = flip(bytes);
    }

    let buf = new ArrayBuffer(bytes.length);
    let view = new DataView(buf);

    bytes.forEach((b, i) => {
        view.setUint8(formatInfo[1] ? i : bytes.length - i - 1, b);
    });

    return converter(view);
}

data.forEach(d => {
    d.formatted = format(d.value);
});

return msg;

